import React from 'react';

export const inputStyle = {

    marginBottom: '20px',
    fontFamily: 'Quicksand',
    border: '1px solid #000000',
    borderRadius: '4px',
    paddingLeft: '10px',
    fontSize: '1rem',
    width: '100%'
}

export const labelStyle = {
    fontFamily: 'Raleway',
    color: '#000000',
    textAlign: 'left',
    // padding: '20px',
    fontWeight: '600',
    fontSize: '12px',
    lineHeight: '18px',
    width: '100%'
}

export const containerStyle = {
    display: 'flex',
    flexFlow: 'row wrap',
    justifyContent: 'center',
    width: '100%'
}

export const formStyle = {
    display: 'flex',
flexDirection: 'column',
alignItems: 'left',
justifyContent: 'center',
// minHeight: '50vh',
width: '100%',
margin: '20px',
maxWidth: '375px'
}
